mapper.update:
update board
set contents = #{contents}
where 1=1
  and id = #{id}

update board
set contents = #{contents}, img = #{img}
where 1=1
  and id = #{id}

update recommended
   set use_yn = #{use_yn}
 where 1=1
   and board_id = #{board_id}
   and user_id = #{user_id}

mapper.select:
	   select count(replynumber) as replyCnt from reply_board
        where 1=1
          and board_id = #{id}

	select id, user_id, use_yn
	  from recommended
 	 where 1=1
 	   and board_id = #{board_id}

	   and user_id = #{user_id}


		and use_yn = #{use_yn}

mapper.delete:
DELETE FROM reply_board
WHERE 1=1
  AND replynumber = #{replynumber};

DELETE FROM recommended
WHERE 1=1
  AND board_id = #{board_id};
